#include <Windows.h>\n#include <Aclapi.h>\n\nint main() {\n    HKEY hKey;\n    LONG result = RegOpenKeyEx(HKEY_LOCAL_MACHINE, "SOFTWARE\\YourKey", 0, KEY_READ, &hKey);\n    if (result == ERROR_SUCCESS) {\n        ACL* pAcl;\n        result = GetSecurityInfo(hKey, SE_REGISTRY_KEY, DACL_SECURITY_INFORMATION, NULL, NULL, &pAcl, NULL, NULL);\n        if (result == ERROR_SUCCESS) {\n            ACCESS_MASK mask = KEY_WRITE;\n            result = AccessCheck(pAcl, NULL, &mask, NULL, NULL, NULL, NULL, NULL);\n            if (result == ERROR_SUCCESS) {\n                // Current user has write access to the key\n            } else {\n                // Current user does not have write access to the key\n            }\n            LocalFree(pAcl);\n        }\n        RegCloseKey(hKey);\n    }\n\n    return 0;\n}