// Assuming your_memory_buffer is a 24-bpp RGB buffer\nunsigned char* output_buffer = new unsigned char[1280 * 800];\n\nfor (int y = 0; y < 800; y++) {\n    for (int x = 0; x < 1280; x++) {\n        unsigned char r = your_memory_buffer[(y * 1280 * 3) + (x * 3)];\n        unsigned char g = your_memory_buffer[(y * 1280 * 3) + (x * 3) + 1];\n        unsigned char b = your_memory_buffer[(y * 1280 * 3) + (x * 3) + 2];\n\n        // Apply Floyd-Steinberg halftoning algorithm\n        int error = (r * 0.299 + g * 0.587 + b * 0.114) - (r >> 3);\n        output_buffer[y * 1280 + x] = (error > 0) ? 255 : 0;\n    }\n}