// Assume hwnd is the handle to the console window\nHDC hdc = GetDC(hwnd);\nRECT rect;\nGetClientRect(hwnd, &rect);\n\n// Set up the font and text color\nHFONT hFont = CreateFont(12, 0, 0, 0, FW_NORMAL, 0, 0, 0, DEFAULT_CHARSET, OUT_DEFAULT_PRECIS, CLIP_DEFAULT_PRECIS, DEFAULT_QUALITY, DEFAULT_PITCH, TEXT("Consolas"));\nSelectObject(hdc, hFont);\nSetTextColor(hdc, RGB(0, 0, 0));\n\n// Draw the text\nTextOut(hdc, 0, 0, TEXT("Hello, World!"), 13);\n\n// Clean up\nDeleteObject(hFont);\nReleaseDC(hwnd, hdc);