// main.cpp\n#include "wrapper.h"\n\nint main() {\n    HMODULE hModule = LoadLibraryA("mydll.dll");\n    if (!hModule) {\n        // error handling\n    }\n\n    void* (*createMyClass)() = (void* (*)())GetProcAddress(hModule, "createMyClass");\n    void (*deleteMyClass)(void*) = (void (*)(void*))GetProcAddress(hModule, "deleteMyClass");\n    void (*myClassFoo)(void*) = (void (*)(void*))GetProcAddress(hModule, "myClassFoo");\n\n    void* myClassPtr = createMyClass();\n    myClassFoo(myClassPtr);\n    deleteMyClass(myClassPtr);\n\n    FreeLibrary(hModule);\n    return 0;\n}