// Assume you have an OpenGL context and a window\n\n// Initialize gDebugger\ngDebuggerInit();\n\n// Get the render tree\nGDebuggerRenderTree* renderTree = gDebuggerGetRenderTree();\n\n// Traverse the render tree\nGDebuggerNode* node = renderTree->GetRootNode();\nwhile (node) {\n    // Print node information\n    printf("%s\n", node->GetName());\n\n    // Recursively traverse child nodes\n    GDebuggerNode* child = node->GetFirstChild();\n    while (child) {\n        // ...\n        child = child->GetNextSibling();\n    }\n\n    node = node->GetNextSibling();\n}\n\n// Cleanup\ngDebuggerShutdown();