// In Resources.dll\nclass Resources {\npublic:\n    static Resources& getInstance() {\n        static Resources instance;\n        return instance;\n    }\n    // ...\n};\n\n// In User.dll\nclass User {\npublic:\n    User() {\n        // Create a dependency on Resources\n        Resources& resources = Resources::getInstance();\n    }\n    ~User() {\n        // Now it's safe to access Resources\n        Resources& resources = Resources::getInstance();\n        // ...\n    }\n};