#include <iostream>\n#include <string>\n#include <vector>\n\nvoid generateSubstrings(const std::string& str, int start, std::string current) {\n  if (start == str.size()) {\n    if (!current.empty()) {\n      std::cout << current << std::endl;\n    }\n    return;\n  }\n\n  // Skip this character\n  generateSubstrings(str, start + 1, current);\n\n  // Include this character\n  if (current.empty() || str[start] >= current.back()) {\n    generateSubstrings(str, start + 1, current + str[start]);\n  }\n}\n\nint main() {\n  std::string str = "aaacdddghzz";\n  generateSubstrings(str, 0, "");\n  return 0;\n}