#include <iostream>\n#include <windows.h>\n\nint main() {\n    // Create a console window\n    AllocConsole();\n\n    // Open the console input stream\n    freopen("CONIN$", "r", stdin);\n\n    // Now you can use std::cin for console input\n    std::string input;\n    std::cout << "Enter some text: ";\n    std::cin >> input;\n    std::cout << "You entered: " << input << std::endl;\n\n    // Don't forget to close the console window when you're done\n    FreeConsole();\n\n    return 0;\n}