// Assume you have a CWnd-derived class, e.g., CMyWindow\nclass CMyWindow : public CWnd\n{\n    // ...\n};\n\n// Create an instance of the window\nCMyWindow myWindow;\n\n// Create another object that will receive the forwarded messages\nclass CMessageReceiver : public CObject\n{\n    // Define a message handler function\n    LRESULT OnMessage(HWND hWnd, UINT uMsg, WPARAM wParam, LPARAM lParam)\n    {\n        // Handle the message here\n        return 0;\n    }\n};\n\nCMessageReceiver receiver;\n\n// Set up the message forwarding\nmyWindow.SetWindowSubclass(&receiver, &CMessageReceiver::OnMessage);