#include <Windows.h>\n#include <Winternl.h>\n\n// Define the callback function\nVOID CALLBACK ProcessNotifyRoutine(PEPROCESS Process, HANDLE ProcessId, PPS_CREATE_NOTIFY_INFO CreateInfo)\n{\n    if (CreateInfo && CreateInfo->FileObjectName && !lstrcmpi(CreateInfo->FileObjectName, L"xyz.exe"))\n    {\n        // Show a message box when xyz.exe starts\n        MessageBox(NULL, L"xyz.exe has started", L"Process Monitor", MB_OK);\n    }\n}\n\nint main()\n{\n    // Register the callback function\n    PsSetCreateProcessNotifyRoutine(ProcessNotifyRoutine, TRUE);\n    // Wait for events\n    while (TRUE)\n    {\n        Sleep(1000);\n    }\n    return 0;\n}