#include <iostream>\n\ntemplate <char... Cs>\nstruct mystruct {\n    static void go() {\n        // Use the char array Cs...\n    }\n};\n\ntemplate <typename T, T... Cs>\nstruct string_literal {\n    using type = mystruct<Cs...>;\n};\n\n#define STR_LITERAL(s) \\n    string_literal<char, s##_impl>::type\n\n#define STR_LITERAL_IMPL(s) \\n    s##_impl = s[0], STR_LITERAL_IMPL2(s, 1)\n\n#define STR_LITERAL_IMPL2(s, i) \\n    i < sizeof(s) ? s[i], STR_LITERAL_IMPL2(s, i + 1) : 0\n\nint main() {\n    STR_LITERAL("asdf")::go();\n    return 0;\n}