#include <iostream>\n#include <string>\n#include <cstdlib>\n\nint main() {\n    // GET request\n    std::string queryString = getenv("QUERY_STRING");\n    if (!queryString.empty()) {\n        std::cout << "GET Request Data: " << queryString << std::endl;\n    }\n\n    // POST request\n    std::string postData;\n    char buffer[1024];\n    int length = 0;\n    while (std::cin.getline(buffer, 1024)) {\n        postData += buffer;\n        length += std::cin.gcount();\n        if (length >= std::atoi(getenv("CONTENT_LENGTH"))) {\n            break;\n        }\n    }\n    if (!postData.empty()) {\n        std::cout << "POST Request Data: " << postData << std::endl;\n    }\n\n    return 0;\n}