// mylib.h\n#pragma once\n\n#include "myallocator.h"\n\nnamespace mylib {\n    class MyClass {\n    public:\n        MyClass() {}\n        ~MyClass() {}\n\n        void* operator new(size_t size) {\n            return mynew(size);\n        }\n\n        void operator delete(void* ptr) {\n            mydelete(ptr);\n        }\n\n        // use the custom allocator for STL containers\n        std::vector<int, allocator<int>> myVector;\n    };\n}