// shared_memory_manager.h\nclass SharedMemoryManager {\npublic:\n    void* allocate(size_t size);\n    void free(void* ptr);\n};\n\n// shared_memory_manager.cpp\nSharedMemoryManager g_memoryManager;\n\nvoid* SharedMemoryManager::allocate(size_t size) {\n    return malloc(size);\n}\n\nvoid SharedMemoryManager::free(void* ptr) {\n    free(ptr);\n}\n\n// dll1.cpp\n#include "shared_memory_manager.h"\n\nvoid* allocateResource() {\n    return g_memoryManager.allocate(1024);\n}\n\n// dll2.cpp\n#include "shared_memory_manager.h"\n\nvoid freeResource(void* ptr) {\n    g_memoryManager.free(ptr);\n}