#include <iostream>\n#include <windows.h>\n\nDWORD WINAPI printSome(LPVOID arg)\n{\n    int i = *(int*)arg;\n    std::cout << i << std::endl;\n    return 0;\n}\n\nvoid spawnThread(LPTHREAD_START_ROUTINE threadName, LPVOID arg)\n{\n    CreateThread(\n        0,      // default security attributes\n        0,          // use default stack size \n        threadName,  // thread function name\n        arg,          // argument to thread function \n        0,          // use default creation flags \n        0       // returns the thread identifier \n    );\n}\n\nint main()\n{\n    int arg = 155;\n    spawnThread(printSome, &arg);\n    Sleep(1000); // wait for the thread to finish\n    return 0;\n}