// Read process memory\nvoid ReadProcessMemory(HANDLE hProcess, LPVOID lpBaseAddress, LPVOID lpBuffer, SIZE_T dwSize) {\n    SIZE_T bytesRead;\n    ReadProcessMemory(hProcess, lpBaseAddress, lpBuffer, dwSize, &bytesRead);\n}\n\n// Write process memory\nvoid WriteProcessMemory(HANDLE hProcess, LPVOID lpBaseAddress, LPVOID lpBuffer, SIZE_T dwSize) {\n    SIZE_T bytesWritten;\n    WriteProcessMemory(hProcess, lpBaseAddress, lpBuffer, dwSize, &bytesWritten);\n}