#include <vector>\n#include <utility> // For std::move\n\ntemplate <typename Iter1, typename Iter2>\nX(Iter1 begin1, Iter1 end1, Iter2 begin2, Iter2 end2)\n    : mVec([begin1, end1, begin2, end2]() {\n        std::vector<Y> v1(begin1, end1);\n        v1.reserve(v1.size() + std::distance(begin2, end2)); // Preallocate space\n        v1.insert(v1.end(), begin2, end2);\n        return v1; // NVRO (Named Return Value Optimization) applies here\n    }()) {}\n\nprivate:\n    const std::vector<Y> mVec;