#include <odb/database.hxx>\n#include <odb/transaction.hxx>\n#include <odb/sqlserver/database.hxx>\n\n#include "mymodels.hxx"\n\nint main()\n{\n  // Create a database instance\n  odb::database* db = new odb::sqlserver::database("mydatabase");\n\n  // Create a transaction\n  odb::transaction t(db->begin());\n\n  // Query the database\n  odb::result<MyModel> result = db->query<MyModel>();\n\n  // Process the results\n  for (odb::result<MyModel>::iterator i = result.begin(); i != result.end(); ++i)\n  {\n    // ...\n  }\n\n  // Commit the transaction\n  t.commit();\n}