#include <iostream>\n#include <cstdlib>\n#include <ctime>\n\nconst int WIDTH = 21;\nconst int HEIGHT = 11;\n\nchar grid[HEIGHT][WIDTH];\n\nvoid createMaze() {\n    // Initialize the grid with a border\n    for (int i = 0; i < HEIGHT; i++) {\n        for (int j = 0; j < WIDTH; j++) {\n            if (i == 0 || i == HEIGHT - 1 || j == 0 || j == WIDTH - 1) {\n                grid[i][j] = '#'; // Border character\n            } else {\n                grid[i][j] = ' '; // Empty cell character\n            }\n        }\n    }\n\n    // Randomly carve out paths\n    for (int i = 1; i < HEIGHT - 1; i++) {\n        for (int j = 1; j < WIDTH - 1; j++) {\n            if (rand() % 2 == 0) {\n                grid[i][j] = ' '; // Carve out a path\n            } else {\n                grid[i][j] = '#'; // Leave a wall\n            }\n        }\n    }\n}\n\nvoid printMaze() {\n    for (int i = 0; i < HEIGHT; i++) {\n        for (int j = 0; j < WIDTH; j++) {\n            std::cout << grid[i][j];\n        }\n        std::cout << std::endl;\n    }\n}\n\nint main() {\n    srand(time(0)); // Seed the random number generator\n\n    createMaze();\n    printMaze();\n\n    return 0;\n}