#include <windows.h>\n#include <Ole2.h>\n\n// Assuming you have the CLSID of the loaded COM object\nCLSID clsid;\n\n// Get the module path\nWCHAR szModulePath[MAX_PATH];\nif (SUCCEEDED(CLSIDFromProgID(clsid, &szModulePath)))\n{\n    wprintf(L"Module Path: %s\n", szModulePath);\n}\n\n// Get the version information\nDLLVERSIONINFO dvi;\nZeroMemory(&dvi, sizeof(dvi));\ndvi.cbSize = sizeof(dvi);\nif (SUCCEEDED(DllGetVersion(clsid, &dvi)))\n{\n    wprintf(L"Version: %d.%d.%d.%d\n", dvi.dwVersionMS >> 16, dvi.dwVersionMS & 0xFFFF, dvi.dwVersionLS >> 16, dvi.dwVersionLS & 0xFFFF);\n}\n\n// Get the description\nIShellItem* pItem;\nif (SUCCEEDED(SHCreateItemFromCLSID(clsid, NULL, IID_PPV_ARGS(&pItem))))\n{\n    LPWSTR lpstrName;\n    if (SUCCEEDED(pItem->GetDisplayName(SIGDN_NORMALDISPLAY, &lpstrName)))\n    {\n        wprintf(L"Description: %s\n", lpstrName);\n        CoTaskMemFree(lpstrName);\n    }\n    pItem->Release();\n}