// NativeArchDetector.cpp\n#include <Windows.h>\n#include <tchar.h>\n\nextern "C" __declspec(dllexport) LPCTSTR GetNativeArchitecture()\n{\n    SYSTEM_INFO sysInfo;\n    GetNativeSystemInfo(&sysInfo);\n    if (sysInfo.wProcessorArchitecture == PROCESSOR_ARCHITECTURE_AMD64)\n    {\n        return _T("x64");\n    }\n    else if (sysInfo.wProcessorArchitecture == PROCESSOR_ARCHITECTURE_INTEL)\n    {\n        return _T("x86");\n    }\n    else\n    {\n        return _T("Unknown");\n    }\n}