#include <Windows.h>\n#include <iostream>\n\nint main() {\n    HKEY hKey;\n    RegOpenKeyEx(HKEY_LOCAL_MACHINE, "SYSTEM\\MountedDevices", 0, KEY_READ, &hKey);\n\n    char valueName[] = "\\DosDevices\\A:";\n    DWORD valueType;\n    DWORD valueSize = 1024;\n    char valueData[1024];\n\n    RegQueryValueEx(hKey, valueName, NULL, &valueType, (LPBYTE)valueData, &valueSize);\n\n    std::cout << "Device ID for drive A: " << valueData << std::endl;\n\n    RegCloseKey(hKey);\n\n    return 0;\n}