// INI file example\n#include <fstream>\n#include <string>\n\nvoid saveSetting(const std::string& key, const std::string& value) {\n    std::ofstream file("settings.ini");\n    if (file.is_open()) {\n        file << key << "=" << value << std::endl;\n        file.close();\n    }\n}\n\nstd::string loadSetting(const std::string& key) {\n    std::ifstream file("settings.ini");\n    if (file.is_open()) {\n        std::string line;\n        while (std::getline(file, line)) {\n            if (line.find(key + "=") == 0) {\n                return line.substr(key.length() + 1);\n            }\n        }\n        file.close();\n    }\n    return "";\n}\n\n// Registry example\n#include <Windows.h>\n\nvoid saveSetting(const std::string& key, const std::string& value) {\n    HKEY hKey;\n    RegCreateKeyEx(HKEY_CURRENT_USER, "Software\\MyApp", 0, NULL, REG_OPTION_NON_VOLATILE, KEY_SET_VALUE, NULL, &hKey, NULL);\n    RegSetValueEx(hKey, key.c_str(), 0, REG_SZ, (LPBYTE)value.c_str(), value.length() + 1);\n    RegCloseKey(hKey);\n}\n\nstd::string loadSetting(const std::string& key) {\n    HKEY hKey;\n    RegOpenKeyEx(HKEY_CURRENT_USER, "Software\\MyApp", 0, KEY_QUERY_VALUE, &hKey);\n    char buffer[1024];\n    DWORD bufferSize = 1024;\n    RegQueryValueEx(hKey, key.c_str(), NULL, NULL, (LPBYTE)buffer, &bufferSize);\n    RegCloseKey(hKey);\n    return std::string(buffer);\n}