#include <iostream>\n\n// Define a logging function with verbosity level\nvoid log(int verbosity, const char* format, ...) {\n    #ifdef VERBOSE\n        if (verbosity <= VERBOSE) {\n            va_list args;\n            va_start(args, format);\n            vprintf(format, args);\n            va_end(args);\n        }\n    #endif\n}\n\n// Usage\nint main() {\n    int i = 10;\n    log(1, "The value is: %d\n", i);\n    return 0;\n}