#include <unordered_map>\n#include <set>\n\nclass Foo;\nclass ID;\n\n// Define the mapping type\nusing IdSetMap = std::unordered_map<Foo*, std::set<ID*>>;\n\n// Create an instance of the map\nIdSetMap idSetMap;\n\nvoid FindXXX(const ID* pID) {\n    // Iterate over the map to find the Foo* associated with the given ID*\n    for (const auto& pair : idSetMap) {\n        if (pair.second.find(pID) != pair.second.end()) {\n            // Found the Foo* associated with the ID*\n            Foo* foo = pair.first;\n            // Do something with foo\n            break;\n        }\n    }\n}