#include "cpphtml/html.hpp"\n\nint main() {\n  cpphtml::HtmlStream stream;\n\n  // Start an HTML document\n  stream << cpphtml::HtmlDoctype() << std::endl;\n\n  // Add a paragraph\n  stream << cpphtml::HtmlTag("p") << "Hello, World!" << std::endl;\n\n  // Add a link\n  stream << cpphtml::HtmlTag("a", "href", "https://www.example.com") << "Example" << std::endl;\n\n  // Output the HTML\n  std::cout << stream.str() << std::endl;\n\n  return 0;\n}