#include <Windows.h>\n\nint main() {\n    // Define the absolute path to your DLL\n    const wchar_t* dllPath = L"C:\\Path\\To\\Your\\DLL.dll";\n\n    // Load the DLL using LoadLibraryEx with the absolute path\n    HMODULE hModule = LoadLibraryEx(dllPath, NULL, LOAD_LIBRARY_SEARCH_APPLICATION_DIR);\n\n    if (hModule == NULL) {\n        // Handle the error\n        return 1;\n    }\n\n    // Use the DLL's functionality...\n    // ...\n\n    // Unload the DLL when finished\n    FreeLibrary(hModule);\n\n    return 0;\n}