#include <Windows.h>\n\nint main() {\n    HKEY hKey;\n    LONG lRes = RegOpenKeyEx(HKEY_CLASSES_ROOT, "TypeLib", 0, KEY_READ, &hKey);\n    if (lRes == ERROR_SUCCESS) {\n        char szValue[256];\n        DWORD dwSize = 256;\n        lRes = RegQueryValueEx(hKey, "1.0\\0\\win32", NULL, NULL, (LPBYTE)&szValue, &dwSize);\n        if (lRes == ERROR_SUCCESS) {\n            std::string strValue(szValue);\n            if (strValue.find("Discovery.dll") != std::string::npos) {\n                // TLB file is registered\n                std::cout << "TLB file is registered" << std::endl;\n            } else {\n                // TLB file is not registered\n                std::cout << "TLB file is not registered" << std::endl;\n            }\n        }\n        RegCloseKey(hKey);\n    }\n    return 0;\n}