// Assuming you have a pointer to the ActiveX control's IUnknown interface\nIUnknown* pUnk = ...;\n\n// Get the IConnectionPointContainer interface\nIConnectionPointContainer* pCPC;\nHRESULT hr = pUnk->QueryInterface(IID_IConnectionPointContainer, (void**)&pCPC);\nif (SUCCEEDED(hr)) {\n    // Get the IConnectionPoint interface for the OnReadyStateChange event\n    IConnectionPoint* pCP;\n    hr = pCPC->FindConnectionPoint(DIID_DWebBrowserEvents2, &pCP);\n    if (SUCCEEDED(hr)) {\n        // Register your event handler\n        hr = pCP->Advise((IDispatch*)yourEventHandler, &yourCookie);\n        if (SUCCEEDED(hr)) {\n            // You're now receiving OnReadyStateChange events\n        }\n    }\n}