#include <fstream>\n#include <string>\n\nint main() {\n    std::ifstream textFile("path/to/your/textfile.txt");\n\n    if (textFile.is_open()) {\n        std::string line;\n        while (std::getline(textFile, line)) {\n            // Process the line\n            // For example, print it to the console\n            std::cout << line << '\n';\n        }\n        textFile.close();\n    } else {\n        std::cerr << "Unable to open file";\n        return 1; // Return with an error code\n    }\n\n    return 0;\n}