#include <Windows.h>\n#include <TlHelp32.h>\n\nbool CheckEnvironmentVariableInProcess(DWORD processId, const char* variableName) {\n    HANDLE hProcess = OpenProcess(PROCESS_QUERY_INFORMATION, FALSE, processId);\n    if (hProcess == NULL) {\n        return false;\n    }\n\n    DWORD size = 0;\n    GetEnvironmentVariableA(variableName, NULL, 0, &size);\n    if (size > 0) {\n        // Variable exists\n        CloseHandle(hProcess);\n        return true;\n    } else {\n        // Variable does not exist\n        CloseHandle(hProcess);\n        return false;\n    }\n}