#include <iostream>\n#include <fstream>\n#include <sstream>\n#include <string>\n#include <map>\n\nint main() {\n    std::ifstream myfile("mydata.txt");\n    std::map<std::string, std::string> FastaMap;\n\n    if (myfile.is_open()) {\n        std::string line;\n        while (std::getline(myfile, line)) {\n            if (line[0] == '>') {\n                std::string Header = line.substr(1);\n                std::string Tag;\n                if (std::getline(myfile, Tag)) {\n                    FastaMap[Header] = Tag;\n                }\n            }\n        }\n        myfile.close();\n    } else {\n        std::cout << "Unable to open file";\n    }\n\n    for (const auto& pair : FastaMap) {\n        std::cout << "Head: " << pair.first << ", End: " << pair.second << std::endl;\n    }\n\n    return 0;\n}