#include <iostream>\n#include <hoard/Hoard.h>\n\nint main() {\n    // Initialize Hoard\n    Hoard::initialize();\n\n    // Allocate memory\n    void* ptr = Hoard::malloc(1024);\n\n    // Use the allocated memory\n    std::cout << "Allocated memory: " << ptr << std::endl;\n\n    // Deallocate memory\n    Hoard::free(ptr);\n\n    // Shutdown Hoard\n    Hoard::shutdown();\n\n    return 0;\n}