#include <iostream>\n#include <vector>\n#include <string>\n#include <numeric>\n\nint main() {\n    std::vector<std::string> vec = {"sample", "string", "for", "this", "example"};\n\n    std::string result = std::accumulate(vec.begin() + 1, vec.end(), vec[0],\n        [](const std::string& a, const std::string& b) {\n            return a + " " + b;\n        });\n\n    std::cout << result << std::endl;\n\n    return 0;\n}