#include <iostream>\n#include <vector>\n#include <string>\n#include <sstream>\n\nint main() {\n    std::vector<std::string> vec = {"sample", "string", "for", "this", "example"};\n\n    std::ostringstream oss;\n    for (const auto& str : vec) {\n        oss << str << " ";\n    }\n\n    std::string result = oss.str();\n    // remove the trailing space\n    if (!result.empty()) {\n        result.pop_back();\n    }\n\n    std::cout << result << std::endl;\n\n    return 0;\n}