#include <windows.h>\n\nint main() {\n    HKEY hKey;\n    LPCTSTR lpSubKey = TEXT("SOFTWARE\\YourCompany\\YourApp");\n    LPCTSTR lpFile = TEXT("C:\\path\\to\\export.reg");\n\n    // Open the key\n    RegOpenKeyEx(HKEY_CURRENT_USER, lpSubKey, 0, KEY_READ, &hKey);\n\n    // Export the key\n    RegExportKey(hKey, lpFile, 0);\n\n    // Import the key\n    RegImportKey(hKey, lpFile, 0);\n\n    // Close the key\n    RegCloseKey(hKey);\n\n    return 0;\n}