#include <Windows.h>\n\nint main() {\n    HANDLE hThread = GetCurrentThread();\n    FILETIME createTime;\n    FILETIME exitTime;\n    FILETIME kernelTime;\n    FILETIME userTime;\n    ULONGLONG pageFaults;\n\n    GetThreadTimes(hThread, &createTime, &exitTime, &kernelTime, &userTime);\n    GetThreadInformation(hThread, ThreadPageFaultCount, &pageFaults, sizeof(pageFaults));\n\n    printf("Page faults: %llu\n", pageFaults);\n\n    return 0;\n}