#include <opencv2/opencv.hpp>\n\nint main() {\n  // Load the image\n  cv::Mat image = cv::imread("input_image.jpg");\n\n  // Define the interpolation method\n  cv::InterpolationFlags interpolationMethod = cv::INTER_CUBIC;\n\n  // Define the desired output resolution\n  int outputWidth = image.cols * 2;\n  int outputHeight = image.rows * 2;\n\n  // Resize the image using interpolation\n  cv::Mat outputImage;\n  cv::resize(image, outputImage, cv::Size(outputWidth, outputHeight), 0, 0, interpolationMethod);\n\n  // Save the output image\n  cv::imwrite("output_image.jpg", outputImage);\n\n  return 0;\n}