#include <windows.h>\n\nvoid simulateMouseClick(int x, int y) {\n    MOUSE_EVENT_RECORD mer;\n    mer.dwMousePosition.X = x;\n    mer.dwMousePosition.Y = y;\n    mer.dwButtonState = FROM_TOUCH;\n    mer.dwFlags = 0;\n    mouse_event(MOUSEEVENTF_LEFTDOWN, 0, 0, 0, 0);\n    mouse_event(MOUSEEVENTF_LEFTUP, 0, 0, 0, 0);\n}\n\nvoid simulateKeyPress(int keyCode) {\n    KEYBD_EVENT_RECORD ker;\n    ker.wVirtualKeyCode = keyCode;\n    ker.wScanCode = MapVirtualKey(keyCode, 0);\n    ker.dwFlags = 0;\n    keybd_event(KEYEVENTF_EXTENDEDKEY, 0, 0, 0, 0);\n    keybd_event(KEYEVENTF_EXTENDEDKEY, 0, 0, 0, 0);\n}