#include <Windows.h>\n\nint main() {\n    HANDLE hDir = CreateFileW(L"C:\\Path\\To\\Directory", GENERIC_READ, FILE_SHARE_READ | FILE_SHARE_WRITE | FILE_SHARE_DELETE, NULL, OPEN_EXISTING, FILE_FLAG_BACKUP_SEMANTICS, NULL);\n    if (hDir == INVALID_HANDLE_VALUE) {\n        // Error handling\n    }\n\n    FILE_NOTIFY_INFORMATION buffer[1024];\n    DWORD bytesReturned;\n    while (ReadDirectoryChangesW(hDir, buffer, sizeof(buffer), TRUE, FILE_NOTIFY_CHANGE_FILE_NAME | FILE_NOTIFY_CHANGE_LAST_WRITE, &bytesReturned, NULL, NULL)) {\n        // Process changes\n        for (DWORD i = 0; i < bytesReturned; i += buffer[i].NextEntryOffset) {\n            // Check if the change is a file modification\n            if (buffer[i].Action == FILE_ACTION_MODIFIED) {\n                // Get the file name\n                WCHAR fileName[MAX_PATH];\n                wcsncpy_s(fileName, MAX_PATH, buffer[i].FileName, buffer[i].FileNameLength / sizeof(WCHAR));\n                fileName[buffer[i].FileNameLength / sizeof(WCHAR)] = L'\0';\n\n                // Process the file modification\n                // ...\n            }\n        }\n    }\n\n    CloseHandle(hDir);\n    return 0;\n}