#include "Poco/Net/MailMessage.h"\n#include "Poco/Net/MailRecipient.h"\n#include "Poco/Net/SMTPClientSession.h"\n\nint main() {\n    Poco::Net::MailMessage message;\n    message.setSender("your_email@example.com");\n    message.addRecipient(Poco::Net::MailRecipient(Poco::Net::MailRecipient::TO, "recipient_email@example.com"));\n    message.setSubject("Test Email");\n    message.setContent("This is a test email sent from C++");\n\n    Poco::Net::SMTPClientSession session("smtp.example.com", 587);\n    session.open();\n    session.login("your_email@example.com", "your_password");\n    session.sendMail(message);\n    session.close();\n\n    return 0;\n}