// Mock for openfile function\nMOCK_CLASS(openfileMock) {\n    MOCK_METHOD(bool, openfile, (const std::string& filename));\n};\n\n// Test case\nTEST_CASE("LoadFileToMem_OpenFileSuccess", "[LoadFileToMem]") {\n    // Arrange\n    std::string filename = "testfile.txt";\n    openfileMock mock;\n    EXPECT_CALL(mock, openfile(filename)).Times(1).WillOnce(Return(true));\n\n    // Act\n    bool result = LoadFileToMem(filename);\n\n    // Assert\n    ASSERT_TRUE(result);\n}