#include <windows.h>\n\nint listFiles(const TCHAR* folderPath) {\n    WIN32_FIND_DATA findData;\n    HANDLE hFind = FindFirstFile(folderPath, &findData);\n    if (hFind == INVALID_HANDLE_VALUE) {\n        return 0;\n    }\n\n    do {\n        // Process the file information\n        TCHAR* fileName = findData.cFileName;\n        // ...\n\n    } while (FindNextFile(hFind, &findData));\n\n    FindClose(hFind);\n    return 1;\n}