#include <SFML/Graphics.hpp>\n\nint main() {\n    // Create a window\n    sf::RenderWindow window(sf::VideoMode(800, 600), "My First GUI App");\n\n    // Create a rectangle\n    sf::RectangleShape rect(sf::Vector2f(50, 50));\n    rect.setFillColor(sf::Color::Red);\n\n    // Main loop\n    while (window.isOpen()) {\n        // Handle events\n        sf::Event event;\n        while (window.pollEvent(event)) {\n            if (event.type == sf::Event::Closed) {\n                window.close();\n            }\n        }\n\n        // Clear the window\n        window.clear();\n\n        // Draw the rectangle\n        window.draw(rect);\n\n        // Display the window\n        window.display();\n    }\n\n    return 0;\n}