#include <windows.h>\n#include <detours.h>\n\n// Define the fake system time\nSYSTEMTIME fakeTime = { 2023, 12, 31, 23, 59, 59, 0, 0 };\n\n// Intercept GetSystemTime API\nDWORD WINAPI GetSystemTimeDetour(SYSTEMTIME* lpSystemTime)\n{\n    *lpSystemTime = fakeTime;\n    return 0;\n}\n\n// Intercept GetSystemTimeAsFileTime API\nDWORD WINAPI GetSystemTimeAsFileTimeDetour(FILETIME* lpFileTime)\n{\n    SYSTEMTIME st = fakeTime;\n    SystemTimeToFileTime(&st, lpFileTime);\n    return 0;\n}\n\nint main()\n{\n    // Initialize Detours\n    DetourRestoreAfterWith();\n    DetourTransactionBegin();\n    DetourUpdateThread(GetCurrentThread());\n\n    // Detour GetSystemTime API\n    DetourAttach(&(PVOID&)GetSystemTime, GetSystemTimeDetour);\n\n    // Detour GetSystemTimeAsFileTime API\n    DetourAttach(&(PVOID&)GetSystemTimeAsFileTime, GetSystemTimeAsFileTimeDetour);\n\n    // Commit the detour transaction\n    DetourTransactionCommit();\n\n    // Run your application that uses the fake system time\n    // ...\n\n    // Clean up Detours\n    DetourTransactionBegin();\n    DetourUpdateThread(GetCurrentThread());\n    DetourDetach(&(PVOID&)GetSystemTime, GetSystemTimeDetour);\n    DetourDetach(&(PVOID&)GetSystemTimeAsFileTime, GetSystemTimeAsFileTimeDetour);\n    DetourTransactionCommit();\n\n    return 0;\n}