#include <Windows.h>\n#include <Dbghelp.h>\n\nint main()\n{\n    HANDLE hFile = CreateFileA("your_executable.exe", GENERIC_READ, FILE_SHARE_READ, NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, NULL);\n    if (hFile == INVALID_HANDLE_VALUE)\n    {\n        // Handle error\n    }\n\n    HANDLE hMap = CreateFileMappingA(hFile, NULL, PAGE_READONLY, 0, 0, NULL);\n    if (hMap == NULL)\n    {\n        // Handle error\n    }\n\n    LPVOID lpBase = MapViewOfFile(hMap, FILE_MAP_READ, 0, 0, 0);\n    if (lpBase == NULL)\n    {\n        // Handle error\n    }\n\n    IMAGE_NT_HEADERS *pNtHeaders = ImageNtHeader(lpBase);\n    if (pNtHeaders == NULL)\n    {\n        // Handle error\n    }\n\n    IMAGE_IMPORT_DESCRIPTOR *pImportDesc = (IMAGE_IMPORT_DESCRIPTOR *)((LPBYTE)lpBase + pNtHeaders->OptionalHeader.DataDirectory[IMAGE_DIRECTORY_ENTRY_IMPORT].VirtualAddress);\n    while (pImportDesc->Name != 0)\n    {\n        char *dllName = (char *)((LPBYTE)lpBase + pImportDesc->Name);\n        // dllName now contains the name of the DLL\n        pImportDesc++;\n    }\n\n    UnmapViewOfFile(lpBase);\n    CloseHandle(hMap);\n    CloseHandle(hFile);\n    return 0;\n}