// DLL code\n#include <Windows.h>\n#include <map>\n\nstd::map<int, int> sharedMap;\n\nHANDLE hSharedMem = CreateFileMapping(INVALID_HANDLE_VALUE, NULL, PAGE_READWRITE, 0, sizeof(sharedMap), L"MySharedMem");\n\nvoid __declspec(dllexport) setMapValue(int key, int value) {\n    // Acquire write lock\n    // ...\n    sharedMap[key] = value;\n    // Release write lock\n}\n\n// Calling process code\n#include <Windows.h>\n\nHANDLE hSharedMem = OpenFileMapping(FILE_MAP_WRITE, FALSE, L"MySharedMem");\n\nvoid* pSharedMem = MapViewOfFile(hSharedMem, FILE_MAP_WRITE, 0, 0, 0);\n\n// Access the shared map\nstd::map<int, int>* pSharedMap = reinterpret_cast<std::map<int, int>*>(pSharedMem);\n\n// Call the exported function to set a value in the shared map\nsetMapValue(1, 10);