#include <Windows.h>\n\nint main() {\n    char buf[MAX_PATH];\n    GetModuleFileName(NULL, buf, sizeof(buf));\n\n    // Get list of all mapped drive letters\n    char driveLetters[MAX_PATH];\n    GetLogicalDriveStrings(sizeof(driveLetters), driveLetters);\n\n    // Find the drive letter that corresponds to the UNC path\n    char* driveLetter = driveLetters;\n    while (*driveLetter) {\n        if (strcmp(buf, driveLetter) == 0) {\n            // Use the drive letter path\n            printf("Drive letter path: %s\n", driveLetter);\n            break;\n        }\n        driveLetter += strlen(driveLetter) + 1;\n    }\n\n    return 0;\n}