#include <Windows.h>\n\n// Define the region to capture\nRECT rect = { 100, 100, 300, 300 }; // x1, y1, x2, y2\n\n// Get the device context\nHDC hdc = GetDC(nullptr);\n\n// Create a bitmap to store the captured region\nHBITMAP hbm = CreateCompatibleBitmap(hdc, rect.right - rect.left, rect.bottom - rect.top);\n\n// Create a memory device context\nHDC memdc = CreateCompatibleDC(hdc);\n\n// Select the bitmap into the memory device context\nHBITMAP oldbm = (HBITMAP)SelectObject(memdc, hbm);\n\n// Capture the screen region\nBitBlt(memdc, 0, 0, rect.right - rect.left, rect.bottom - rect.top, hdc, rect.left, rect.top, SRCCOPY);\n\n// Release resources\nSelectObject(memdc, oldbm);\nDeleteDC(memdc);\nReleaseDC(nullptr, hdc);\n\n// Now you can access the bitmap pixels using GetPixel or GetDIBits