// Define the WAP Push message\nTBuf8<1024> pushMessage;\npushMessage.Append(0x06); // UDH\npushMessage.Append(0x05); // UDH\npushMessage.Append(0x25); // WAP Push Indicator\npushMessage.Append(0x0F); // WAP Push Indicator\npushMessage.Append(0x84); // Port number (2948 for WAP Push)\npushMessage.Append(0x23); // Port number\npushMessage.Append(0xF0); // Port number\n\n// Add the WAP Push content\npushMessage.Append(0xFD); // WAP Push PDU type (Push Sync)\npushMessage.Append(0x06); // WAP Push PDU length\n// ... add the WAP Push content here ...\n\n// Set the message destination port to 2948 (WAP Push port)\niMessage->SetDestinationPort(2948);\n\n// Send the message\niMessage->Des().Append(pushMessage);